import React, { useState } from 'react';
import './LoginForm.css';
import { FaLock, FaUser } from "react-icons/fa";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const LoginForm = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');

  const handleUserIdChange = (e) => {
    const value = e.target.value;
    // Si el valor tiene una longitud de 9, agregamos la clase "valid" al input, de lo contrario, la eliminamos.
    if (value.length === 9) {
      e.target.classList.add('valid');
    } else {
      e.target.classList.remove('valid');
    }
    setUserId(value);
  };

  const validateUserId = (id) => {
    const regex = /^\d{9}$/;
    if (!regex.test(id)) {
      return 'El ID de usuario debe contener exactamente 9 dígitos.';
    }
    return '';
  };

  const validatePassword = (pass) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!regex.test(pass)) {
      return 'La contraseña debe tener al menos 8 caracteres, una mayúscula, un número y un carácter especial.';
    }
    return '';
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const userIdError = validateUserId(userId);
    const passwordError = validatePassword(password);

    if (userIdError) {
      toast.error(userIdError);
    }

    if (passwordError) {
      toast.error(passwordError);
    }

    if (!userIdError && !passwordError) {
      setTimeout(() => {
        toast.success('¡Hola!', {
          position: "top-center"
        });
      }, 500);
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={handleSubmit}>
        <h1>Iniciar Sesión</h1>
        <div className="input-box">
          <input
            type="text"
            placeholder="ID de Usuario"
            value={userId}
            onChange={handleUserIdChange}
            required
          />
          <FaUser className='icon' />
        </div>
        <div className="input-box">
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <FaLock className='icon' />
        </div>
        <div className="remember-forgot">
          <a href='#'>¿Olvidaste tu contraseña?</a>
        </div>
        <button type="submit">Iniciar Sesión</button>
        <div className="register-link">
          <p>¿No tienes una cuenta? -{'>'} <a href='#'>Regístrate</a></p>
        </div>
      </form>
      <ToastContainer position="top-center" />
    </div>
  );
};

export default LoginForm;
