import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './DashboardPage.css';

const DashboardPage = () => {
  const [selectedBeach, setSelectedBeach] = useState('');
  const [beaches, setBeaches] = useState([]);
  const [rating, setRating] = useState(0);
  const [visitDate, setVisitDate] = useState('');
  const [comment, setComment] = useState('');

  useEffect(() => {
    // Cargar lista de playas al montar el componente
    const fetchBeaches = async () => {
      try {
        const response = await axios.get('http://localhost:3001/playas');
        setBeaches(response.data);
      } catch (error) {
        console.error('Error fetching beaches:', error);
      }
    };
    fetchBeaches();
  }, []);

  const handleSubmit = async () => {
    if (!selectedBeach || rating === 0 || !visitDate || !comment) return;

    try {
      await axios.post('http://localhost:3001/reviews', {
        userId: '014417519', // Suponiendo un usuario específico por ahora
        beachId: selectedBeach.id,
        rating,
        visitDate,
        comment
      });
      alert('Reseña enviada exitosamente');
      // Limpiar los campos después de enviar la reseña
      setRating(0);
      setVisitDate('');
      setComment('');
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('Error al enviar la reseña');
    }
  };

  return (
    <div className="dashboard-container">
      <h2>Dashboard</h2>
      <label>Selecciona una playa:</label>
      <select value={selectedBeach} onChange={(e) => setSelectedBeach(JSON.parse(e.target.value))}>
        <option value="">Seleccione una playa</option>
        {beaches.map(beach => (
          <option key={beach.id} value={JSON.stringify(beach)}>{beach.nombre}</option>
        ))}
      </select>
      {selectedBeach && (
        <div>
          <h3>{selectedBeach.nombre}</h3>
          <p>Ubicación: {selectedBeach.ubicacion}</p>
          <p>Actividades Recomendadas: {selectedBeach.actividadesRecomendadas}</p>
          <p>Link: <a href={selectedBeach.link} target="_blank" rel="noopener noreferrer">Visitar</a></p>
          <img src={selectedBeach.img} alt={selectedBeach.nombre} />
        </div>
      )}
      <div>
        <label>Calificación:</label>
        <input type="number" value={rating} onChange={(e) => setRating(parseInt(e.target.value))} min="0" max="10" />
      </div>
      <div>
        <label>Fecha de Visita:</label>
        <input type="date" value={visitDate} onChange={(e) => setVisitDate(e.target.value)} />
      </div>
      <div>
        <label>Comentario:</label>
        <textarea value={comment} onChange={(e) => setComment(e.target.value)} />
      </div>
      <button onClick={handleSubmit}>Aceptar</button>
    </div>
  );
};

export default DashboardPage;
