import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './DashboardPage.css';

const DashboardPage = ({ userId }) => {
  const [beaches, setBeaches] = useState([]);
  const [selectedBeach, setSelectedBeach] = useState(null);
  const [rating, setRating] = useState(0);
  const [visitDate, setVisitDate] = useState('');
  const [comment, setComment] = useState('');

  useEffect(() => {
    const fetchBeaches = async () => {
      try {
        const response = await axios.get('http://localhost:3001/playas');
        setBeaches(response.data);
      } catch (error) {
        console.error('Error al obtener la lista de playas:', error);
      }
    };

    fetchBeaches();
  }, []);

  const handleSubmit = async () => {
    if (!selectedBeach) return;

    try {
      await axios.post('http://localhost:3001/reviews', {
        userId,
        beachId: selectedBeach.id,
        rating,
        visitDate,
        comment
      });
      alert('Rese単a enviada exitosamente');
    } catch (error) {
      console.error('Error submitting review:', error);
      alert('Error al enviar la rese単a');
    }
  };

  return (
    <div className="dashboard-page">
      <h1>Dashboard de Playas</h1>
      <div className="beach-selector">
        <h2>Selecciona una Playa</h2>
        <select onChange={e => setSelectedBeach(beaches.find(beach => beach.id === parseInt(e.target.value)))}>
          <option value="">Seleccionar</option>
          {beaches.map(beach => (
            <option key={beach.id} value={beach.id}>{beach.nombre}</option>
          ))}
        </select>
      </div>

      {selectedBeach && (
        <div className="beach-info">
          <h2>{selectedBeach.nombre}</h2>
          <p>{selectedBeach.ubicacion}</p>
          <a href={selectedBeach.link} target="_blank" rel="noopener noreferrer">Ver Video</a>
          <img src={`/path/to/images/${selectedBeach.img}`} alt={selectedBeach.nombre} />
          <p>Actividades Recomendadas: {selectedBeach.actividadesRecomendadas}</p>
        </div>
      )}

      {selectedBeach && (
        <div className="review-form">
          <h2>Calificar {selectedBeach.nombre}</h2>
          <div>
            <label>Calificaci坦n: {rating}</label>
            <input
              type="range"
              min="0"
              max="10"
              value={rating}
              onChange={e => setRating(parseInt(e.target.value))}
            />
          </div>
          <div>
            <label>Fecha de Visita</label>
            <input
              type="date"
              value={visitDate}
              onChange={e => setVisitDate(e.target.value)}
              max={new Date().toISOString().split('T')[0]}
              min="1950-01-01"
            />
          </div>
          <div>
            <label>Comentarios</label>
            <textarea
              value={comment}
              onChange={e => setComment(e.target.value)}
            />
          </div>
          <button onClick={handleSubmit}>Aceptar</button>
        </div>
      )}
    </div>
  );
};

export default DashboardPage;
