import React, { useState } from 'react';
import './DashboardPage.css';
import playas from './playas.json'; // Importa el archivo JSON

const DashboardPage = () => {
  const [step, setStep] = useState(1);
  const [selectedBeach, setSelectedBeach] = useState('');
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [visitedDate, setVisitedDate] = useState('');
  const [newsletter, setNewsletter] = useState(false);

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handlePrevStep = () => {
    setStep(step - 1);
  };

  const handleRatingChange = (e) => {
    const value = e.target.value;
    if (value === '' || (Number(value) >= 0 && Number(value) <= 100)) {
      setRating(value);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (rating !== '' && Number(rating) >= 0 && Number(rating) <= 100) {
      alert(`Calificación enviada: ${rating}\nComentario: ${comment}\nFecha de visita: ${visitedDate}\nSuscripción al boletín: ${newsletter ? 'Sí' : 'No'}`);
    } else {
      alert('Por favor, ingrese una calificación válida entre 0 y 100.');
    }
  };

  return (
    <div className="dashboard-container">
      <div className="form-container">
        {step === 1 && (
          <>
            <h2>Selecciona una Playa</h2>
            <select
              value={selectedBeach}
              onChange={(e) => setSelectedBeach(e.target.value)}
            >
              <option value="">Seleccione una playa</option>
              {playas.map((beach, index) => (
                <option key={index} value={beach.nombre}>
                  {beach.nombre}
                </option>
              ))}
            </select>
            {selectedBeach && (
              <div>
                <p>{playas.find((beach) => beach.nombre === selectedBeach).ubicacion}</p>
                {playas.find((beach) => beach.nombre === selectedBeach).actividadRecomendada && (
                  <p>Actividad Recomendada: {playas.find((beach) => beach.nombre === selectedBeach).actividadRecomendada}</p>
                )}
                <button onClick={handleNextStep}>Siguiente</button>
              </div>
            )}
          </>
        )}

        {step === 2 && (
          <>
            <h2>Califica la Playa</h2>
            <label htmlFor="rating">Calificación</label>
            <input
              type="number"
              id="rating"
              value={rating}
              onChange={handleRatingChange}
              min="0"
              max="100"
            />

            <label htmlFor="comment">Comentario</label>
            <textarea
              id="comment"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
            ></textarea>

            <label htmlFor="visitedDate">Fecha de Visita</label>
            <input
              type="date"
              id="visitedDate"
              value={visitedDate}
              onChange={(e) => setVisitedDate(e.target.value)}
            />

            <label htmlFor="newsletter">Suscribirse al boletín</label>
            <input
              type="checkbox"
              id="newsletter"
              checked={newsletter}
              onChange={(e) => setNewsletter(e.target.checked)}
            />

            <button onClick={handlePrevStep} className="back-button">Atrás</button>
            <button onClick={handleSubmit}>Aceptar</button>
          </>
        )}
      </div>
    </div>
  );
};

export default DashboardPage;
