import React, { useState, useEffect } from 'react';
import './DashboardPage.css';
import playasData from '../Data/playas.json'; // Importar el archivo playas.json

const DashboardPage = () => {
  const [step, setStep] = useState(1);
  const [selectedBeach, setSelectedBeach] = useState('');
  const [rating, setRating] = useState('');

  const beaches = playasData; // Usar la información del archivo playas.json

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handlePrevStep = () => {
    setStep(step - 1);
  };

  const handleRatingChange = (e) => {
    const value = e.target.value;
    if (value === '' || (Number(value) >= 0 && Number(value) <= 100)) {
      setRating(value);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (rating !== '' && Number(rating) >= 0 && Number(rating) <= 100) {
      alert(`Calificación enviada: ${rating}`);
    } else {
      alert('Por favor, ingrese una calificación válida entre 0 y 100.');
    }
  };

  return (
    <div className="dashboard-container">
      <div className="form-container">
        {step === 1 && (
          <>
            <h2>Selecciona una Playa</h2>
            <select
              value={selectedBeach}
              onChange={(e) => setSelectedBeach(e.target.value)}
            >
              <option value="">Seleccione una playa</option>
              {beaches.map((beach) => (
                <option key={beach.id} value={beach.nombre}>
                  {beach.nombre}
                </option>
              ))}
            </select>
            {selectedBeach && (
              <div>
                <img src={require(`../Assets/${selectedBeach.toLowerCase()}.jpg`).default} alt={selectedBeach} />
                <p>{beaches.find((beach) => beach.nombre === selectedBeach).ubicacion}</p>
                <p>Información: {beaches.find((beach) => beach.nombre === selectedBeach).info}</p>
                <button onClick={handleNextStep}>Siguiente</button>
              </div>
            )}
          </>
        )}

        {step === 2 && (
          <>
            <h2>Califica la Playa</h2>
            <label htmlFor="rating">Calificación</label>
            <input
              type="number"
              id="rating"
              value={rating}
              onChange={handleRatingChange}
              min="0"
              max="100"
            />
            <button onClick={handlePrevStep} className="back-button">Atrás</button>
            <button onClick={handleSubmit}>Aceptar</button>
          </>
        )}
      </div>
    </div>
  );
};

export default DashboardPage;
