import React, { useEffect, useState } from 'react';
import axios from 'axios';

const DashboardPage = ({ userId }) => {
  const [playas, setPlayas] = useState([]);
  const [selectedPlaya, setSelectedPlaya] = useState(null);
  const [calificacion, setCalificacion] = useState(0);
  const [comentario, setComentario] = useState('');
  const [fecha, setFecha] = useState('');

  useEffect(() => {
    const fetchPlayas = async () => {
      try {
        const response = await axios.get('http://localhost:3001/playas');
        setPlayas(response.data);
      } catch (error) {
        console.error('Error al obtener la lista de playas:', error);
      }
    };
    fetchPlayas();
  }, []);

  const handleSubmit = async () => {
    try {
      const review = {
        userId,
        playaId: selectedPlaya.id,
        calificacion,
        comentario,
        fecha,
      };
      await axios.post('http://localhost:3001/reviews', review);
      alert('Evaluación enviada exitosamente');
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div>
      <h1>Evaluar Playas</h1>
      <div>
        <h2>Selecciona una playa</h2>
        <ul>
          {playas.map(playa => (
            <li key={playa.id} onClick={() => setSelectedPlaya(playa)}>
              {playa.nombre}
            </li>
          ))}
        </ul>
      </div>

      {selectedPlaya && (
        <div>
          <h2>Información de la playa</h2>
          <img src={`/${selectedPlaya.img}`} alt={selectedPlaya.nombre} />
          <p>Nombre: {selectedPlaya.nombre}</p>
          <p>Ubicación: {selectedPlaya.ubicacion}</p>
          <p>Actividades recomendadas: {selectedPlaya.actividadesRecomendadas}</p>
          <p>
            <a href={selectedPlaya.link} target="_blank" rel="noopener noreferrer">
              Ver video
            </a>
          </p>

          <h2>Evaluar esta playa</h2>
          <label>
            Calificación:
            <input
              type="range"
              min="0"
              max="10"
              value={calificacion}
              onChange={(e) => setCalificacion(e.target.value)}
            />
            {calificacion}
          </label>
          <label>
            Fecha de visita:
            <input
              type="date"
              value={fecha}
              onChange={(e) => setFecha(e.target.value)}
              max={new Date().toISOString().split('T')[0]}
              min="1950-01-01"
            />
          </label>
          <label>
            Comentario:
            <textarea
              value={comentario}
              onChange={(e) => setComentario(e.target.value)}
            ></textarea>
          </label>
          <button onClick={handleSubmit}>Enviar evaluación</button>
        </div>
      )}
    </div>
  );
};

export default DashboardPage;
