import React, { useState, useEffect } from 'react';
import './DashboardPage.css';

const DashboardPage = () => {
  const [step, setStep] = useState(1);
  const [selectedBeach, setSelectedBeach] = useState('');
  const [rating, setRating] = useState(0);
  const [visitDate, setVisitDate] = useState('');
  const [comment, setComment] = useState('');
  const [subscribe, setSubscribe] = useState(false);
  const [beaches, setBeaches] = useState([]);

  useEffect(() => {
    // Hacer la solicitud para obtener las playas
    fetch('../playas.json')
      .then(response => {
        if (!response.ok) {
          throw new Error('Error al obtener la lista de playas');
        }
        return response.json();
      })
      .then(data => {
        setBeaches(data);
      })
      .catch(error => {
        console.error('Error al obtener la lista de playas:', error);
      });
  }, []);

  const handleNextStep = () => {
    setStep(step + 1);
  };

  const handlePrevStep = () => {
    setStep(step - 1);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí puedes agregar la lógica para enviar los datos a los archivos JSON
    alert('Evaluación enviada exitosamente');
  };

  return (
    <div className="dashboard-container">
      <div className="form-container">
        {step === 1 && (
          <>
            <h2>Selecciona una Playa</h2>
            <select
              value={selectedBeach}
              onChange={(e) => setSelectedBeach(e.target.value)}
            >
              <option value="">Seleccione una playa</option>
              {beaches.map((beach, index) => (
                <option key={index} value={beach.nombre}>
                  {beach.nombre}
                </option>
              ))}
            </select>
            {selectedBeach && (
              <div>
                <p>{beaches.find((beach) => beach.nombre === selectedBeach).info}</p>
                <button onClick={handleNextStep}>Siguiente</button>
              </div>
            )}
          </>
        )}

        {step === 2 && (
          <>
            <h2>Califica la Playa</h2>
            <label htmlFor="rating">Calificación (del 0 al 10)</label>
            <input
              type="range"
              id="rating"
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              min="0"
              max="10"
            />
            <output htmlFor="rating">{rating}</output>
            <label htmlFor="visitDate">Fecha de visita</label>
            <input
              type="date"
              id="visitDate"
              value={visitDate}
              onChange={(e) => setVisitDate(e.target.value)}
              max={new Date().toISOString().split('T')[0]}
              min="1950-01-01"
            />
            <label htmlFor="comment">Comentario</label>
            <textarea
              id="comment"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
            ></textarea>
            <div className="checkbox-container">
              <label htmlFor="subscribe">¿Deseas inscribirte al boletín de la playa?</label>
              <input
                type="checkbox"
                id="subscribe"
                checked={subscribe}
                onChange={(e) => setSubscribe(e.target.checked)}
              />
            </div>
            <button onClick={handlePrevStep} className="back-button">Atrás</button>
            <button onClick={handleSubmit}>Aceptar</button>
          </>
        )}
      </div>
    </div>
  );
};

export default DashboardPage;
