import React, { useState } from 'react';
import './LoginForm.css';
import { FaLock, FaUser } from "react-icons/fa";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';
import { Link } from 'react-router-dom';

const LoginForm = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');

  const handleUserIdChange = (e) => {
    const value = e.target.value;
    const inputId = document.getElementById("inputId");
    inputId.addEventListener("keypress", (e) => {
      const idCar = /[A-Za-z]/;
      if(!idCar.test(e.key)) e.preventDefault();
    })
    if (value.length === 9) {
      e.target.classList.add('valid');
    } else {
      e.target.classList.remove('valid');
    }
    setUserId(value);
  };

  const validateUserId = (id) => {
    const regex = /^\d{9}$/;
    if (!regex.test(id)) {
      return 'El ID de usuario debe contener exactamente 9 dígitos.';
    }
    return '';
  };

  const validatePassword = (pass) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!regex.test(pass)) {
      return 'La contraseña debe tener al menos 8 caracteres, una mayúscula, un número y un carácter especial.';
    }
    return '';
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const userIdError = validateUserId(userId);
    const passwordError = validatePassword(password);

    if (userIdError) {
      toast.error(userIdError);
    }

    if (passwordError) {
      toast.error(passwordError);
    }

    if (!userIdError && !passwordError) {
      setTimeout(() => {
        toast.success('¡Hola!', {
          position: "top-center"
        });
      }, 500);
    }
  };

  const handleForgotPassword = async () => {
    const userIdError = validateUserId(userId);
    if (userIdError) {
      toast.error(userIdError);
      return;
    }

    try {
      const response = await axios.post('http://localhost:3001/forgot-password', { userId });
      if (response.status === 200) {
        toast.success('Correo de recuperación enviado.');
      }
    } catch (error) {
      toast.error('Error al enviar el correo de recuperación.');
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={handleSubmit}>
        <h1>Iniciar Sesión</h1>
        <div className="input-box">
          <input
            type="text"
            placeholder="ID de Usuario"
            value={userId}
            onChange={handleUserIdChange}
            required
            maxLength={9}
            pattern="[0-9]*"
            className={userId.length === 9 ? 'valid' : ''}
          />
          <FaUser className='icon' />
        </div>
        <div className="input-box">
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <FaLock className='icon' />
        </div>
        <div className="remember-forgot">
          <p onClick={handleForgotPassword} style={{ cursor: 'pointer' }}>¿Olvidaste tu contraseña?</p>
        </div>
        <button type="submit">Iniciar Sesión</button>
        <div className="register-link">
          <p>¿No tienes una cuenta? -{'>'} <Link to="/register">Regístrate</Link></p>
        </div>
      </form>
      <ToastContainer position="top-center" />
    </div>
  );
};

export default LoginForm;
