import React, { useState } from 'react';
import './DashboardPage.css';

const DashboardPage = () => {
  const [places, setPlaces] = useState([
    {
      id: 1,
      name: 'Puerto Escondido',
      location: 'Oaxaca, México',
      extension: '5 km',
      type: 'playa',
      ratings: {
        cleanliness: 4,
        beauty: 5,
        accessibility: 3,
        safety: 4,
      },
    },
    {
      id: 2,
      name: 'Sayulita',
      location: 'Nayarit, México',
      extension: '3 km',
      type: 'playa',
      ratings: {
        cleanliness: 4,
        beauty: 4,
        accessibility: 4,
        safety: 3,
      },
    },
    // Agregar más lugares si es necesario
  ]);

  const handleRatingChange = (placeId, aspect, value) => {
    setPlaces(prevPlaces =>
      prevPlaces.map(place => {
        if (place.id === placeId) {
          return {
            ...place,
            ratings: {
              ...place.ratings,
              [aspect]: value,
            },
          };
        }
        return place;
      })
    );
  };

  return (
    <div className="dashboard-container">
      <div className="background-image"></div>
      <div className="form-container wrapper">
        <h1>Lugares con Playas</h1>
        <div className="places-list">
          {places.map(place => (
            <div key={place.id} className="place">
              <h2>{place.name}</h2>
              <p><strong>Ubicación:</strong> {place.location}</p>
              <p><strong>Extensión:</strong> {place.extension}</p>
              <p><strong>Tipo:</strong> {place.type}</p>
              <div className="ratings">
                <p>
                  Limpieza:
                  <input
                    type="number"
                    min="1"
                    max="5"
                    value={place.ratings.cleanliness}
                    onChange={(e) => handleRatingChange(place.id, 'cleanliness', parseInt(e.target.value))}
                  />
                </p>
                <p>
                  Belleza:
                  <input
                    type="number"
                    min="1"
                    max="5"
                    value={place.ratings.beauty}
                    onChange={(e) => handleRatingChange(place.id, 'beauty', parseInt(e.target.value))}
                  />
                </p>
                <p>
                  Accesibilidad:
                  <input
                    type="number"
                    min="1"
                    max="5"
                    value={place.ratings.accessibility}
                    onChange={(e) => handleRatingChange(place.id, 'accessibility', parseInt(e.target.value))}
                  />
                </p>
                <p>
                  Seguridad:
                  <input
                    type="number"
                    min="1"
                    max="5"
                    value={place.ratings.safety}
                    onChange={(e) => handleRatingChange(place.id, 'safety', parseInt(e.target.value))}
                  />
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
