import React, { useState } from 'react';
import './RegisterForm.css';
import { FaUser, FaLock } from "react-icons/fa";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

const RegisterForm = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleUserIdChange = (e) => {
    const value = e.target.value;
    if (!/^\d*$/.test(value)) {
      return;
    }
    if (value.length <= 9) {
      setUserId(value);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Enviar la solicitud HTTP POST para registrar al usuario
      const response = await axios.post('http://localhost:3001/register', {
        userId: userId,
        password: password
      });

      // Si el registro fue exitoso, mostrar un mensaje de éxito
      if (response.status === 200) {
        toast.success('Registro exitoso.');
        // Limpiar los campos después del registro exitoso
        setUserId('');
        setPassword('');
        setConfirmPassword('');
      }
    } catch (error) {
      // Si ocurre un error durante el registro, mostrar un mensaje de error
      toast.error('Error en el registro.');
      console.error(error);
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={handleSubmit}>
        <h1>Registro</h1>
        <div className="input-box">
          <input
            type="text"
            placeholder="ID de Usuario"
            value={userId}
            onChange={handleUserIdChange}
            required
            maxLength={9}
            className={userId.length === 9 ? 'valid' : ''}
          />
          <FaUser className='icon' />
        </div>
        <div className="input-box">
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <FaLock className='icon' />
        </div>
        <div className="input-box">
          <input
            type="password"
            placeholder="Confirmar Contraseña"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          <FaLock className='icon' />
        </div>
        <button type="submit">Registrarse</button>
      </form>
      <ToastContainer position="top-center" />
    </div>
  );
};

export default RegisterForm;
