import React, { useState } from 'react';
import './LoginForm.css';
import { FaLock, FaUser } from "react-icons/fa";

export const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({ username: '', password: '', form: '' });

  const validateUsername = (name) => {
    const regex = /^[A-Za-z]+$/;
    if (!regex.test(name)) {
      return 'El nombre de usuario solo puede contener letras.';
    }
    return '';
  };

  const validatePassword = (pass) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!regex.test(pass)) {
      return 'La contraseña debe tener al menos 8 caracteres, una mayúscula, un número y un carácter especial.';
    }
    return '';
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const usernameError = validateUsername(username);
    const passwordError = validatePassword(password);
    setErrors({ username: usernameError, password: passwordError });

    if (!usernameError && !passwordError) {
      // Lógica de autenticación
      fetch('http://localhost:3000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          console.log('Inicio de sesión exitoso');
        } else {
          setErrors({ ...errors, form: 'Nombre de usuario o contraseña incorrectos' });
        }
      })
      .catch(error => {
        console.error('Error:', error);
        setErrors({ ...errors, form: 'Error en el servidor. Inténtelo de nuevo más tarde.' });
      });
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={handleSubmit}>
        <h1>pLallaZ en UajAka</h1>
        <div className="input-box">
          <input
            type="text"
            placeholder="Nombre de Usuario"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <FaUser className='icon' />
          {errors.username && <p className="error">{errors.username}</p>}
        </div>
        <div className="input-box">
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <FaLock className='icon' />
          {errors.password && <p className="error">{errors.password}</p>}
        </div>
        <div className="remember-forgot">
          <a href='#'>¿Olvidaste tu contraseña?</a>
        </div>
        <button type="submit">Iniciar Sesión</button>
        {errors.form && <p className="error">{errors.form}</p>}
        <div className="register-link">
          <p>¿No tienes una cuenta?<a href='#'> -{'>'} Regístrate</a></p>
        </div>
      </form>
    </div>
  );
};
