import React, { useState } from 'react';
import './RegisterForm.css';
import { FaUser, FaLock } from "react-icons/fa";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

const RegisterForm = () => {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');

  const handleUserIdChange = (e) => {
    const value = e.target.value;
    if (!/^\d*$/.test(value)) {
      // Si el valor contiene algo que no es un número, no lo actualizamos
      return;
    }
    if (value.length <= 9) {
      setUserId(value);
    }
  };

  const validateUserId = (id) => {
    const regex = /^\d{9}$/;
    if (!regex.test(id)) {
      return 'El ID de usuario debe contener exactamente 9 dígitos.';
    }
    return '';
  };

  const validatePassword = (pass) => {
    const regex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
    if (!regex.test(pass)) {
      return 'La contraseña debe tener al menos 8 caracteres, una mayúscula, un número y un carácter especial.';
    }
    return '';
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const userIdError = validateUserId(userId);
    const passwordError = validatePassword(password);

    if (userIdError) {
      toast.error(userIdError);
    }

    if (passwordError) {
      toast.error(passwordError);
    }

    if (!userIdError && !passwordError) {
      try {
        // Aquí puedes enviar los datos de registro al servidor
        // Por ejemplo:
        // await axios.post('/api/register', { userId, password });
        toast.success('Registro exitoso.');
        // Limpia los campos después del registro exitoso
        setUserId('');
        setPassword('');
      } catch (error) {
        toast.error('Error al registrar.');
      }
    }
  };

  return (
    <div className='wrapper'>
      <form onSubmit={handleSubmit}>
        <h1>Registro</h1>
        <div className="input-box">
          <input
            type="text"
            placeholder="ID de Usuario"
            value={userId}
            onChange={handleUserIdChange}
            required
            maxLength={9}
            className={userId.length === 9 ? 'valid' : ''}
          />
          <FaUser className='icon' />
        </div>
        <div className="input-box">
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <FaLock className='icon' />
        </div>
        <button type="submit">Registrarse</button>
      </form>
      <ToastContainer position="top-center" />
    </div>
  );
};

export default RegisterForm;
